/** @format */

import React, { useState } from "react";
import ExpenseDate from "../Expenses/ExpenseDate";
import Card from "../UI/Card";
import "../css/ExpenseItem.css";

const ExpenseItem = (props) => {
	// React hooks must be called inside a component function
	const [title, setTitle] = useState(props.title);

	//when the button is clicked this function is called
	//because we passed it as a pointer to the onClik event
	//state is sparareted per component basis
	const clickHandler = () => {
		setTitle("update");
	};

	return (
		<Card className="expense-item">
			<ExpenseDate date={props.date} />
			<div className="expense-item__description">
				<h2>{title}</h2>
				<div className="expense-item__price">{props.amount}</div>
				{/* we just point to the funcion here we dont call it   */}
				<button onClick={clickHandler}>Change title</button>
			</div>
		</Card>
	);
};

export default ExpenseItem;
